"use strict";var light=$(".nav__right--contact--opening-hours--light"),label=$(".nav__right--contact--opening-hours--light span"),date=new Date,day=date.getDay(),hour=date.getHours(),minutes=date.getMinutes();function updateLight(){10<=hour&&hour<22&&changeStatus("open-now"),changeStatus(6==day&&9<=hour&&hour<22?"open-now":22==hour&&minutes<30?"closing-soon":"closed")}function changeStatus(t){light.classList.add(t),label.textContent=t.replace("-"," ")}updateLight(),setTimeout(updateLight,3600);