"use strict";var light=$(".nav__right--contact--opening-hours--light"),label=$(".nav__right--contact--opening-hours--light span"),hour=(new Date).getHours(),minutes=(new Date).getMinutes();function updateLight(){changeStatus(10<=hour&&hour<22?"open-now":22==hour&&minutes<30?"closing-soon":"closed")}function changeStatus(t){light.classList.add(t),label.textContent=t.replace("-"," ")}updateLight(),setTimeout(updateLight,3600);